import React from 'react'

class TextArea extends React.Component {
  render() {
    return (
      <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={this.props.updateLength}></textarea>
      )
  }
}

class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      input: this.makeTextArea(),
      inputLength: 0
    }

    this.updateLength = this.updateLength.bind(this)
  }

  updateLength() {
    this.setState(() => ({
      inputLength: this.input.value.length
    }))
  }

  render() {
    return <div style={{'text-align': 'left', color: '#666'}}>
              <TextArea updateLength={this.updateLength}/>
              <span>{this.state.inputLength} / 500</span>
            </div>
  }

  makeTextArea() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={() => this.updateLength()}></textarea>

  }




}


export default TextWithCounter;
