import React from 'react'


class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      value: ''
    }

    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(e) {
    this.onChange(e)
  }

  render() {
    return <div style={{'textAlign': 'left', color: '#666'}}>
              <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" value={this.value} onChange={this.handleChange} />
              <span>{this.value ? this.value.length : 0} / 500</span>
            </div>
  } 

}


export default TextWithCounter;
