import React from 'react'


class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      textLength: 0
    }

    this.updateLength = this.updateLength.bind(this)
  }

  updateLength() {
    this.setState( () => ({ textLength: 10}))
  }

  render() {
    return <div style={{'text-align': 'left', color: '#666'}}>
              <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={(text) => console.log(text)}></textarea>
              <span>{this.state.textLength} / 500</span>
            </div>
  }

  makeTextArea() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={(text) => console.log(text)}></textarea>

  }




}


export default TextWithCounter;
