import React from 'react'

class TextArea extends React.Component {
  render() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={this.props.updateLength()}></textarea>
  }
}

class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      textLength: 0
    }

  }

  updateLength() {
    this.setState( () => ({ textLength: 10}))
  }

  render() {
    return <div style={{'text-align': 'left', color: '#666'}}>
              <TextArea  updateLength={this.updateLength} />
              <span>{this.state.textLength} / 500</span>
            </div>
  }

  makeTextArea() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={() => this.updateLength()}></textarea>

  }




}


export default TextWithCounter;
