import React from 'react'
import PropTypes from 'prop-types'


const styles = {
  counter: {
    height: 11,
    position: 'absolute',
    bottom: -12,
    right: 0,
    fontSize: 11,
    lineHeight: '11px',
    color: '#C0C0C0',
  }
}

class TextWithCounter extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      valueLength: 0
    }
  }

  componentDidMount() {
    const input = (<textarea className="form-control" id="inputMessage" placeholder="Message" rows="4"></textarea>)
    const setValueLength = () => {this.setState({valueLength: input.value.length})}
    input.addEventListener('keyup', setValueLength)
    setValueLength()
  }

  render(){
    return {input}
  }
}

export default TextWithCounter;
