import React from 'react'
import PropTypes from 'prop-types'


class TextWithCounter extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      input: makeTextArea()
    }
  }

  render(){
    return <div style={{'text-align': 'left', color: '#666'}}>
              {this.state.input}
              <span>{this.state.input.value ? this.state.input.value.length : 0}</span>
            </div>
  }
}

function makeTextArea() {
  return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4"></textarea>

}

export default TextWithCounter;
