import React from 'react'


class TextWithCounter extends React.Component {

  makeTextArea() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={() => 0}></textarea>

  }

  constructor(props) {
    super(props)
    this.state = {
      input: makeTextArea(),
      inputLength: 0
    }
  }

  render(){
    return <div style={{'text-align': 'left', color: '#666'}}>
              {this.state.input}
              <span>{this.state.inputLength} / 500</span>
            </div>
  }


}


export default TextWithCounter;
