import React from 'react'


class CharCounter extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      valueLength: 0
      }

  }

  componentDidMount() {
    const input = this.container.firstChild
    const setValueLength = () => {this.setState({valueLength: input.value.length})}
  }

  updateValue() {
    return 0
  }

  render(){
    return(
      <View>

        <TextInput
          multiline = {true}
          numberOfLines = {6}
          maxLength = {130}
          placeholder='I am awesome because...'
          value={this.state.value}
          onChangeText={(value) => this.setState({value})}

        />
        <Text>
          Characters Left:{this.state.value.length}/130
        </Text>
      </View>
    )
  }
}

export default CharCounter;
