import React from 'react'
import './Form.css'
import TextWithCounter from './TextWithCounter.js'
import axios from 'axios'

class Form extends React.Component{

  constructor(props) {
    super(props)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  onChange = (e) => {
    this.setState({[e.target.name]: e.target.value})
  }

  handleSubmit(event) {
    console.log('submit')
    return false
  }

  render() {
     return <div>
      <form className="mx-auto" id="form">
        <h1 className="form-title">Feedback</h1>
        <div className="form-group">
          <input type="text" className="form-control" name="fname" placeholder="First Name" value=""/><br />
          <input type="text" className="form-control" name="lname" placeholder="Last Name" /><br />
          <input type="email" className="form-control" name="email" placeholder="john@example.com" /><br />
          <TextWithCounter name="message"></TextWithCounter><br />
          <button type="submit" className="btn btn-primary" onClick={this.handleSubmit}>Submit</button>
        </div>
      </form> 
    </div>
  }
}

export default Form;
