import React from 'react'
import './Form.css'
import axios from 'axios'

class Form extends React.Component{

  constructor(props) {
    super(props)
    this.state = {
      fname: '',
      lname: '',
      email: '',
      message: ''
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }

  handleChange(e) {
    this.setState({[e.target.name]: e.target.value})
  }

  handleSubmit(e) {
    e.preventDefault()
    const { fname, lname, email, message} = this.state
    if (fname && lname && email && message.length <= 500) {
      var formData = new FormData();
      formData.set('first', fname)
      formData.set('last', lname)
      formData.set('email', email)
      formData.set('message', message)
      axios({
        method: 'post',
        url: 'https://52.15.184.142:80/feedback',
        timeout: 5000,
        data: formData
      })
      .then(function(response) {
        console.log('POST SUCCESS')
        console.log(response.data)
      })
      .catch(function(response) {
        console.log('POST ERROR:')
        console.log(response)
      })
    } else {
      console.log('invalid')
    }
    console.log('end')
    return false
  }

  render() {
    const { fname, lname, email, message } = this.state
     return <div>
      <form className="mx-auto" id="form">
        <h1 className="form-title">Feedback</h1>
        <div className="form-group">
          <input 
            type="text" 
            className="form-control" 
            name="fname" 
            placeholder="First Name" 
            value={fname}
            onChange={this.handleChange}
          /><br />
          <input 
            type="text" 
            className="form-control" 
            name="lname" 
            placeholder="Last Name" 
            value={lname}
            onChange={this.handleChange}
          /><br />
          <input 
            type="email" 
            className="form-control" 
            name="email" 
            placeholder="john@example.com" 
            value={email}
            onChange={this.handleChange}
          /><br />
          <TextWithCounter
            name="message"
            onChange={this.handleChange}
            value={message}>
          </TextWithCounter><br />
          <button 
            type="submit" 
            className="btn btn-primary" 
            onClick={this.handleSubmit}>
            Submit
          </button>
        </div>
      </form> 
    </div>
  }
}

function TextWithCounter(props) {
  return <div style={{'textAlign': 'left', color: '#666'}}>
            <textarea className="form-control" 
                      id="inputMessage" 
                      placeholder="Message" 
                      rows="4" 
                      name={props.name}
                      value={props.value} 
                      onChange={props.onChange} />
            <span>{props.value.length} / 500</span>
          </div>
}

export default Form;
