import React from 'react'


class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      textValue: ''
    }

    this.updateLength = this.updateLength.bind(this)
  }

  updateLength(event) {
    console.log(this.props.value + ' |' + this.state.textValue)
    this.props.onChange(event)
    this.setState({ textValue: event.target.value})
  }

  render() {
    return <div style={{'textAlign': 'left', color: '#666'}}>
              <textarea className="form-control" 
                        id="inputMessage" 
                        placeholder="Message" 
                        rows="4" 
                        name={this.props.name}
                        value={this.props.value} 
                        onChange={this.props.onChange} />
              <span>{this.props.value.length} / 500</span>
            </div>
  } 


}


export default TextWithCounter;
