import React from 'react'


class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      textValue: ''
    }

    this.onChange = this.onChange.bind(this)
  }

  onChange(event) {
    this.setState({ textValue: event.target.value})
    this.onChange(event);
  }

  render() {
    return <div style={{'textAlign': 'left', color: '#666'}}>
              <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" value={this.state.textValue} onChange={this.updateLength} />
              <span>{this.state.textValue.length} / 500</span>
            </div>
  } 


}


export default TextWithCounter;
