import React from 'react'


class TextWithCounter extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      input: this.makeTextArea(),
      inputLength: 0
    }
  }

  render(){
    return <div style={{'text-align': 'left', color: '#666'}}>
              {this.state.input}
              <span>{this.state.inputLength} / 500</span>
            </div>
  }

  makeTextArea() {
    return <textarea className="form-control" id="inputMessage" placeholder="Message" rows="4" onChange={() => this.updateLength()}></textarea>

  }

  updateLength() {
    let newVal = this.state.input.value ? this.state.input.value.length : 0
    this.setState((state, props) => ({
      inputLength: state.input.value ? state.input.value.length : 0
    }))
  }


}


export default TextWithCounter;
